import Aside from "@/components/Aside";
import Header from "@/components/Header";
export default {
  name: 'Manage',
  data: function data() {
    return {
      collapseBtnClass: 'el-icon-s-fold',
      isCollapse: false,
      sideWidth: 250,
      user: {}
    };
  },
  components: {
    Aside: Aside,
    Header: Header
  },
  created: function created() {
    // 从后台获取最新的User数据
    this.getUser();
  },
  methods: {
    collapse: function collapse() {
      this.isCollapse = !this.isCollapse;

      if (this.isCollapse) {
        this.sideWidth = 64;
        this.collapseBtnClass = 'el-icon-s-unfold';
      } else {
        this.sideWidth = 250;
        this.collapseBtnClass = 'el-icon-s-fold';
      }
    },
    getUser: function getUser() {
      var _this = this;

      var username = localStorage.getItem("user") ? JSON.parse(localStorage.getItem("user")).username : "";

      if (username) {
        // 从后台获取User数据
        this.request.get("/user/username/" + username).then(function (res) {
          // 重新赋值后台的最新User数据
          _this.user = res.data;
        });
      }
    }
  }
};
